      if(weight != null && height !=null){

        imcCalculator()
        setMedia1(null)
        setMedia2(null)
        setMedia3(null)
        setMessageMedia("Sua media é igual:")
        setTextButton("Calcular Novamente")
        return
      }
      setMedia(null)
      setTextButton("Calcular")
      setMessageMedia("preencha os campos")
    }

    //front end / campos de texto e botões
  
    return (

      //aqui fica os campos para preencher e o botão calcular

      //keyboard type e o tipo de teclado, especificamente o teclado numerico

      <View style={styles.formContext}>
            <View style={styles.form}>


                 {/*campo de digitar*/}
                 <TextInput 
                 style={styles.input}
                 onChangeText={setMedia1}
                 value={Media1}
                 placeholder='Digite a primeira nota.'
                 keyboardType='numeric'//determinado o tipo de teclado
                 
                 />
                 
                 
                 {/*campo de digitar*/}
                 <TextInput
                   style={styles.input}
                   onChangeText={setMedia2}
                   value={Media2}
                   placeholder='Digite a segunda nota.'
                   keyboardType='numeric'//determinado o tipo de teclado

                 />

                 {/*campo de digitar*/}
                 <TextInput
                   style={styles.input}
                   onChangeText={setMedia3}
                   value={Media3}
                   placeholder='Digite a terceira nota.'
                   keyboardType='numeric'//determinado o tipo de teclado

                 />
               
                 {/*botãos com efeito*/}
                 <TouchableOpacity 
                    style={styles.buttonCalculator}
                    onPress={() =>{
                      validationMedia()
                    }}
                 >

                 {/*texto dentro do botão*/}  
                 <Text style={styles.textbuttonCalculator}>{TextButton}</Text>
                 
                 </TouchableOpacity>
                 
            </View> 

           {/*resultado do imc*/}
           <ResultMedia messageResultImc={messageMedia} ResultMedia={Media}/> 

      </View>

    );
  }
